<div class="popup -triangle-bc">
    <div class="popup__button icons -messages" ng-class="{'-active': client.state.chatOpen}" ng-click="client.toggleChat()"><span class="-icon"></span></div>
    <div class="popup__button icons -volume" ng-class="{'-active': !client.state.muted}" ng-click="client.listen()"><span class="-icon"></span></div>
    <div class="popup__button icons -video2" ng-class="{'-active': client.position.inView}" ng-click="client.placeIntoView()"><span class="-icon"></span></div>
</div>